mapper.select:
SELECT B.*,U.name AS writer_name FROM tb_board B INNER JOIN tb_user U ON B.writer_id=U.user_id
B.title LIKE CONCAT('%',#{title},'%')
ORDER BY B.idx DESC LIMIT #{startList},#{listSize};

mapper.insert:

			INSERT INTO tb_board
   				(title,content,writer_id)
			VALUE
				(#{title},#{content},#{writerId});



				SELECT LAST_INSERT_ID();

mapper.delete:

DELETE FROM
	tb_board_att_file
WHERE
	idx=#{idx}
	AND board_idx=#{boardIdx};
